// Menu.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import Filters from "./Filters";
import ItemCard from "./ItemCard";
import CartModal from "./CartModal";

export default function Menu() {
  const [categories, setCategories] = useState([]);
  const [items, setItems] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [filterItems, setFilterItems] = useState([]);
  const [filterIngredients, setFilterIngredients] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [modalItem, setModalItem] = useState(null);

  useEffect(() => {
    // Fetch categories, items, and ingredients from the backend API
    const fetchData = async () => {
      try {
        const [catRes, itemRes, ingrRes] = await Promise.all([
          axios.get("/api/categories"),
          axios.get("/api/items"),
          axios.get("/api/ingredients"),
        ]);
        setCategories(catRes.data);
        setItems(itemRes.data);
        setIngredients(ingrRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    // Filter items based on selected filters
    let filtered = items;

    if (selectedCategory) {
      filtered = filtered.filter(
        (item) => item.CategoryId === selectedCategory
      );
    }

    if (filterItems.length > 0) {
      filtered = filtered.filter((item) => filterItems.includes(item.Id));
    }

    if (filterIngredients.length > 0) {
      filtered = filtered.filter((item) =>
        filterIngredients.every((ingId) => item.IngredientIds.includes(ingId))
      );
    }

    setFilteredItems(filtered);
  }, [items, selectedCategory, filterItems, filterIngredients]);

  const handleAddToCart = (item) => {
    setModalItem(item);
  };

  const handleOrder = (itemWithDetails) => {
    setCart([...cart, itemWithDetails]);
    setModalItem(null);
  };

  return (
    <div className="menu-container">
      <aside className="filters-sidebar">
        <Filters
          categories={categories}
          ingredients={ingredients}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
          filterItems={filterItems}
          setFilterItems={setFilterItems}
          filterIngredients={filterIngredients}
          setFilterIngredients={setFilterIngredients}
        />
      </aside>
      <main className="menu-main">
        {selectedCategory ? (
          <h2>{categories.find((cat) => cat.Id === selectedCategory)?.Name}</h2>
        ) : (
          <h2>All Categories</h2>
        )}
        <div className="items-grid">
          {filteredItems.map((item) => (
            <ItemCard key={item.Id} item={item} onAddToCart={handleAddToCart} />
          ))}
        </div>
      </main>
      {modalItem && (
        <CartModal
          item={modalItem}
          ingredients={ingredients}
          onClose={() => setModalItem(null)}
          onOrder={handleOrder}
        />
      )}
    </div>
  );
}
