import axios from 'axios';

// Create an axios instance
const api = axios.create({
  baseURL: 'https://localhost:5000/api',
  withCredentials: true // Ensure cookies (refresh token) are sent with requests
});

// Intercept responses to handle access token refresh
api.interceptors.response.use(
  response => response,
  async (error) => {
    const originalRequest = error.config;

    // If the response was unauthorized and we haven't retried yet
    if (error.response.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      try {
        // Attempt to refresh the token
        const { data } = await axios.post('/auth/refresh-token', {}, { withCredentials: true });
        
        // Store the new access token
        localStorage.setItem('token', data.token);
        
        // Update the original request with the new token and retry it
        originalRequest.headers['Authorization'] = `Bearer ${data.token}`;
        return api(originalRequest);
      } catch (refreshError) {
        // If refresh fails, log out and redirect to login
        localStorage.removeItem('token');
        window.location.href = '/login';
        return Promise.reject(refreshError);
      }
    }

    return Promise.reject(error);
  }
);

export default api;
