import MenuItemTile from "./MenuItemTile";
import { useState } from "react";
import FlyingButton from "react-flying-item";
import toast from "react-hot-toast";

export default function MenuItem({
  Id,
  image,
  name,
  description,
  basePrice,
  sizes,
  extraIngredientPrices,
  onAddToCart,
}) {
  const [selectedSize, setSelectedSize] = useState(sizes?.[0] || null);
  const [selectedExtras, setSelectedExtras] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  async function handleAddToCartButtonClick() {
    const hasOptions = sizes.length > 0 || extraIngredientPrices.length > 0;
    if (hasOptions && !showPopup) {
      setShowPopup(true);
      return;
    }

    const selectedOptions = {
      size: selectedSize,
      extras: selectedExtras,
    };

    await onAddToCart({ Id, name }, selectedOptions);
    setShowPopup(false);
  }

  function handleExtraThingClick(ev, extraThing) {
    const checked = ev.target.checked;
    if (checked) {
      setSelectedExtras((prev) => [...prev, extraThing]);
    } else {
      setSelectedExtras((prev) => prev.filter((e) => e.Id !== extraThing.Id));
    }
  }

  let selectedPrice = basePrice;
  if (selectedSize) {
    selectedPrice += selectedSize.Price;
  }
  if (selectedExtras?.length > 0) {
    selectedExtras.forEach((extra) => {
      selectedPrice += extra.Price;
    });
  }

  return (
    <>
      {showPopup && (
        <div
          onClick={() => setShowPopup(false)}
          className="fixed inset-0 bg-black/80 flex items-center justify-center"
        >
          <div
            onClick={(ev) => ev.stopPropagation()}
            className="my-8 bg-white p-4 rounded-lg max-w-md"
          >
            <div
              className="overflow-y-scroll p-4"
              style={{ maxHeight: "calc(100vh - 100px)" }}
            >
              <h2 className="text-lg font-bold text-center mb-2">{name}</h2>
              <p className="text-center text-gray-500 text-sm mb-2">
                {description}
              </p>
              {sizes?.length > 0 && (
                <div className="py-2">
                  <h3 className="text-center text-gray-700">Pick your size</h3>
                  {sizes.map((size) => (
                    <label
                      key={size.Id}
                      className="flex items-center gap-2 p-4 border rounded-md mb-1"
                    >
                      <input
                        type="radio"
                        onChange={() => setSelectedSize(size)}
                        checked={selectedSize?.Id === size.Id}
                        name="size"
                      />
                      {size.Name} ${basePrice + size.Price}
                    </label>
                  ))}
                </div>
              )}
              {extraIngredientPrices?.length > 0 && (
                <div className="py-2">
                  <h3 className="text-center text-gray-700">Any extras?</h3>
                  {extraIngredientPrices.map((extraThing) => (
                    <label
                      key={extraThing.Id}
                      className="flex items-center gap-2 p-4 border rounded-md mb-1"
                    >
                      <input
                        type="checkbox"
                        onChange={(ev) => handleExtraThingClick(ev, extraThing)}
                        checked={selectedExtras.some(
                          (e) => e.Id === extraThing.Id
                        )}
                        name={extraThing.Name}
                      />
                      {extraThing.Name} +${extraThing.Price}
                    </label>
                  ))}
                </div>
              )}
              <FlyingButton targetTop={"5%"} targetLeft={"95%"} src={image}>
                <div
                  className="primary sticky bottom-2 cursor-pointer"
                  onClick={handleAddToCartButtonClick}
                >
                  Add to cart ${selectedPrice.toFixed(2)}
                </div>
              </FlyingButton>
              <button className="mt-2" onClick={() => setShowPopup(false)}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
      <MenuItemTile
        onAddToCart={handleAddToCartButtonClick}
        {...{
          image,
          name,
          description,
          basePrice,
          sizes,
          extraIngredientPrices,
        }}
      />
    </>
  );
}
