// Filters.js
import React from "react";

export default function Filters({
  categories,
  ingredients,
  selectedCategory,
  setSelectedCategory,
  filterItems,
  setFilterItems,
  filterIngredients,
  setFilterIngredients,
}) {
  const handleCategoryChange = (e) => {
    const value = parseInt(e.target.value);
    setSelectedCategory(value === 0 ? null : value);
  };

  const handleItemFilterChange = (e) => {
    const value = parseInt(e.target.value);
    if (e.target.checked) {
      setFilterItems([...filterItems, value]);
    } else {
      setFilterItems(filterItems.filter((id) => id !== value));
    }
  };

  const handleIngredientFilterChange = (e) => {
    const value = parseInt(e.target.value);
    if (e.target.checked) {
      setFilterIngredients([...filterIngredients, value]);
    } else {
      setFilterIngredients(filterIngredients.filter((id) => id !== value));
    }
  };

  return (
    <div>
      <h3>Filters</h3>
      <div className="filter-section">
        <h4>Categories</h4>
        <select onChange={handleCategoryChange} value={selectedCategory || 0}>
          <option value={0}>All Categories</option>
          {categories.map((cat) => (
            <option key={cat.Id} value={cat.Id}>
              {cat.Name}
            </option>
          ))}
        </select>
      </div>
      <div className="filter-section">
        <h4>Items</h4>
        {categories
          .flatMap((cat) => cat.Items || [])
          .map((item) => (
            <div key={item.Id}>
              <input
                type="checkbox"
                id={`item-${item.Id}`}
                value={item.Id}
                checked={filterItems.includes(item.Id)}
                onChange={handleItemFilterChange}
              />
              <label htmlFor={`item-${item.Id}`}>{item.Name}</label>
            </div>
          ))}
      </div>
      <div className="filter-section">
        <h4>Ingredients</h4>
        {ingredients.map((ing) => (
          <div key={ing.Id}>
            <input
              type="checkbox"
              id={`ingredient-${ing.Id}`}
              value={ing.Id}
              checked={filterIngredients.includes(ing.Id)}
              onChange={handleIngredientFilterChange}
            />
            <label htmlFor={`ingredient-${ing.Id}`}>{ing.Name}</label>
          </div>
        ))}
      </div>
    </div>
  );
}
