import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import Profile from './Profile';

function About() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
      const token = localStorage.getItem('token');
      setIsAuthenticated(!!token);
    }, []);
  
    const handleLogout = async () => {
      try {
        await fetch('/api/auth/logout', {
          method: 'POST',
          credentials: 'include',
        });
  
        localStorage.removeItem('token');
        setIsAuthenticated(false);
      } catch (error) {
        console.error("Error during logout:", error);
      }
    };
    
  return (
    <Router>
      <header className="flex items-center justify-between mx-4 my-2">
        <nav className="flex items-center gap-8 text-primary font-semibold">
          <Link className="font-semibold text-2xl text-primary" to="/">BREWTIFUL</Link>
          <Link to="/">Home</Link>
          <Link to="/menu">Menu</Link>
          <Link to="/about">About</Link>
        </nav>
        <nav className="flex items-center gap-4 text-primary font-semibold">
          {isAuthenticated ? (
            <>
              <Link to="/profile">Profile</Link>
              <button onClick={handleLogout} className="bg-primary rounded text-white px-6 py-2">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register" className="bg-primary rounded text-white px-6 py-2">Register</Link>
            </>
          )}
        </nav>
      </header>
      <Routes>
      <Route path="/about" element={
      <div className="container mx-auto mt-8">
    <section className="bg-white p-6 rounded shadow-md mb-8">
      <h2 class="text-xl font-bold text-teal-600 mb-4">Sistemos paskirtis</h2>
      <p>Projekto tikslas - sukurti puslapį, kuris būtų skirtas užsisakyti gėrimus iš kavinės.</p>
      <p>Veikimo principas – kuriamą sistemą sudaro dvi dalys: internetinis puslapis, kuriuo
        naudosis svečiai, vartotojai, administratorius bei aplikacijų programavimo sąsaja (API).
      </p>
    </section>

    <section id="menu" className="bg-white p-6 rounded shadow-md mb-8">
      <h2 className="text-xl font-bold text-teal-600 mb-4">Funkciniai reikalavimai</h2>
      <div>
        <h3 className="text-lg font-semibold mb-2">Svečias</h3>
        <ul className="list-disc list-inside">
          <li>Peržiūrėti kategorijų sąrašą.</li>
          <li>Užsiregistruoti.</li>
        </ul>
      </div>
      <div className="mt-4">
        <h3 className="text-lg font-semibold mb-2">Prisijungęs vartotojas</h3>
        <ul className="list-disc list-inside">
          <li>Užsisakyti gėrimus.</li>
          <li>Keisti gėrimų sudėtį.</li>
          <li>Modifikuoti užsakymų krepšelį.</li>
          <li>Kaupti taškus nemokamui gėrimui.</li>
          <li>Atsijungti.</li>
        </ul>
      </div>
      <div className="mt-4">
        <h3 className="text-lg font-semibold mb-2">Administratorius</h3>
        <ul className="list-disc list-inside">
          <li>Kurti naują kategoriją.</li>
          <li>Modifikuoti kategorijas.</li>
          <li>Ištrinti kategorijas.</li>
          <li>Kurti naujus produktus.</li>
          <li>Modifikuoti produktus.</li>
          <li>Ištrinti produktus.</li>
        </ul>
      </div>
    </section>

    <section id="architecture" className="bg-white p-6 rounded shadow-md">
      <h2 className="text-xl font-bold text-teal-600 mb-4">Sistemos architektūra</h2>
      <p>Sistemos sudedamosios dalys:</p>
      <ul className="list-disc list-inside">
        <li><strong>Kliento pusė</strong> – naudojant React.js, Tailwind;</li>
        <li><strong>Serverio pusė</strong> – naudojant <span className="text-gray-800 font-semibold">ASP.NET</span> Core. Duomenų bazė – MongoDB.</li>
      </ul>
    </section>
  </div>
   } />


        <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/profile" element={isAuthenticated ? <Profile /> : 
          <div>
            <h1 className='font-bold text-red-700'>Please login to view your profile.</h1>
          </div>
        } />
      </Routes>
    </Router>
  );
}

export default About;
