import React, { useState, useEffect } from "react";
import api from "../axiosInstance";

export default function Menu() {
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [items, setItems] = useState([]);
    const [cart, setCart] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [ingredientAdjustments, setIngredientAdjustments] = useState({});

    useEffect(() => {
        // Fetch categories on mount
        api.get("/Category")
            .then(response => setCategories(response.data))
            .catch(error => console.error("Error fetching categories:", error));
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            // Fetch items for the selected category
            api.get(`/Item/category/${selectedCategory.id}`)
                .then(response => setItems(response.data))
                .catch(error => console.error("Error fetching items:", error));
        }
    }, [selectedCategory]);

    useEffect(() => {
        // Load cart from localStorage
        const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
        setCart(savedCart);
    }, []);

    useEffect(() => {
        // Save cart to localStorage
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    const handleAddToCart = item => {
        setSelectedItem(item);
        const adjustments = {};
        item.ingredients.forEach(ingredient => {
            adjustments[ingredient.id] = ingredient.defaultQuantity;
        });
        setIngredientAdjustments(adjustments);
    };

    const handleIngredientChange = (ingredientId, quantity) => {
        setIngredientAdjustments(prev => ({
            ...prev,
            [ingredientId]: Math.max(quantity, 0)
        }));
    };

    const calculatePrice = () => {
        if (!selectedItem) return 0;
        let basePrice = selectedItem.price;
        for (const [ingredientId, quantity] of Object.entries(ingredientAdjustments)) {
            const ingredient = selectedItem.ingredients.find(ing => ing.id === parseInt(ingredientId));
            if (quantity > ingredient.defaultQuantity) {
                basePrice += (quantity - ingredient.defaultQuantity) * ingredient.extraCost;
            }
        }
        return basePrice;
    };

    const handleAddToOrder = () => {
        const itemToAdd = {
            ...selectedItem,
            modifiedIngredients: Object.entries(ingredientAdjustments).map(([id, quantity]) => ({
                ingredientId: parseInt(id),
                quantity
            })),
            totalPrice: calculatePrice()
        };
        setCart(prevCart => [...prevCart, itemToAdd]);
        setSelectedItem(null); // Close the popup
    };

    return (
        <div className="menu-page" style={{ display: "flex", padding: "20px" }}>
            {/* Filters */}
            <div className="filters" style={{ width: "20%", marginRight: "20px" }}>
                <h3>Filters</h3>
                <div>
                    <h4>Categories</h4>
                    {categories.map(category => (
                        <div key={category.id}>
                            <button onClick={() => setSelectedCategory(category)}>
                                {category.name}
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Menu Items */}
            <div className="menu-items" style={{ width: "60%" }}>
                <h2>{selectedCategory ? selectedCategory.name : "Select a Category"}</h2>
                {items.map(item => (
                    <div key={item.id} style={{ display: "flex", justifyContent: "space-between", marginBottom: "10px" }}>
                        <span>{item.name}</span>
                        <span>${item.price.toFixed(2)}</span>
                        <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
                    </div>
                ))}
            </div>

            {/* Cart Popup */}
            {selectedItem && (
                <div>
                    {/* Backdrop */}
                    <div
                        style={{
                            position: "fixed",
                            top: 0,
                            left: 0,
                            width: "100%",
                            height: "100%",
                            backgroundColor: "rgba(0, 0, 0, 0.5)"
                        }}
                        onClick={() => setSelectedItem(null)}
                    ></div>
                    {/* Popup */}
                    <div
                        className="cart-popup"
                        style={{
                            position: "fixed",
                            top: "20%",
                            left: "30%",
                            width: "40%",
                            padding: "20px",
                            border: "1px solid #ccc",
                            backgroundColor: "#fff"
                        }}
                    >
                        <h3>{selectedItem.name}</h3>
                        <div>
                            <h4>Adjust Ingredients</h4>
                            {selectedItem.ingredients.map(ingredient => (
                                <div key={ingredient.id} style={{ marginBottom: "10px" }}>
                                    <span>{ingredient.name}</span>
                                    <input
                                        type="number"
                                        value={ingredientAdjustments[ingredient.id]}
                                        min="0"
                                        onChange={e => handleIngredientChange(ingredient.id, parseInt(e.target.value) || 0)}
                                    />
                                    {ingredient.extraCost > 0 && (
                                        <span>+${ingredient.extraCost.toFixed(2)}/unit</span>
                                    )}
                                </div>
                            ))}
                        </div>
                        <h4>Total Price: ${calculatePrice().toFixed(2)}</h4>
                        <button onClick={handleAddToOrder}>Add to Order</button>
                        <button onClick={() => setSelectedItem(null)}>Cancel</button>
                    </div>
                </div>
            )}

            {/* Cart Summary */}
            <div className="cart-summary" style={{ width: "20%", marginLeft: "20px" }}>
                <h3>Cart</h3>
                {cart.map((cartItem, index) => (
                    <div key={index} style={{ marginBottom: "10px" }}>
                        <span>{cartItem.name}</span>
                        <ul>
                            {cartItem.modifiedIngredients.map(mod => (
                                <li key={mod.ingredientId}>
                                    {
                                        selectedItem?.ingredients.find(ing => ing.id === mod.ingredientId)
                                            ?.name
                                    }
                                    : {mod.quantity}
                                </li>
                            ))}
                        </ul>
                        <span>${cartItem.totalPrice.toFixed(2)}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}
