import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token'); // Adjust token storage as per your authentication flow
        console.log("Token:", localStorage.getItem('token')); // Add this to debug
        const response = await axios.get('https://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setProfile(response.data);
      } catch (err) {
        let errorMessage = 'Failed to fetch profile information';
        if (err.response && err.response.data && err.response.data.message) {
          errorMessage += `: ${err.response.data.message}`;
        } else if (err.message) {
          errorMessage += `: ${err.message}`;
        } else {
          errorMessage += ': Unknown error';
        }
        setError(errorMessage);
      }
    };

    fetchProfile();
  }, []);

  if (error) return <div>{error}</div>;
  if (!profile) return <div>Loading...</div>;

  return (
    <section className="mt-8">
    <h1 className="text-center text-primary text-4xl mb-4">Profile</h1>
    <form className="max-w-md mx-auto">
        <div className="flex gap-4 items-center">
            <div>
                <div className="p-2 rounded-lg relative">
                    <Image
                        className="rounded-lg w-full h-full mb-1"
                        src="/path/to/avatar.png"
                        width={250}
                        height={250}
                        alt="avatar"
                    />
                    <button type="button">Edit</button>
                </div>
            </div>
            <div className="grow">
                 <h1>{profile.Name}'s Profile</h1>
                 <p>Email: {profile.UserLogin.Email}</p>
                 <p>Role: {profile.Role}</p>
            </div>
        </div>
    </form>
</section>
  );
};

export default Profile;
