import React, { useEffect, useState } from "react";
import api from "../axiosInstance";
import monitorToken from "./monitorToken";
import Modal from "../components/Modal";
import { useCart } from "./CartContext";

function Menu() {
  const [profile, setProfile] = useState(null);
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalTitle, setModalTitle] = useState("");
  const { addToCart } = useCart();

  useEffect(() => {
    monitorToken();

    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");

        // Fetch user profile
        const { data: profileData } = await api.get("/auth/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(profileData);

        if (profileData.role === "Admin") {
          setModalMessage("Admins should use the Admin Orders page.");
          setModalTitle("Access Denied");
          setIsModalOpen(true);
          return;
        }

        // Fetch categories
        const { data: categoryData } = await api.get("/Category", {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Convert categories into a mapping of id -> name
        const categoryMap = categoryData.reduce((map, category) => {
          map[category.id] = category.name;
          return map;
        }, {});
        setCategories(categoryMap);

        // Fetch items
        const { data: itemsData } = await api.get("/Item", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const itemsWithDynamicQuantities = itemsData.map((item) => ({
          ...item,
          ingredients: item.ingredients.map((ing) => ({
            ...ing,
            quantity: ing.defaultQuantity,
          })),
        }));
        setItems(itemsWithDynamicQuantities);
      } catch (error) {
        console.error("Error fetching data:", error);
        setModalMessage("Failed to fetch data.");
        setModalTitle("Error");
        setIsModalOpen(true);
      }
    };

    fetchData();
  }, []);

  const groupedItems = items.reduce((acc, item) => {
    const category = categories[item.categoryId] || "Uncategorized";
    if (!acc[category]) acc[category] = [];
    acc[category].push(item);
    return acc;
  }, {});

  if (!profile)
    return <div className="text-center mt-8 text-gray-700">Loading...</div>;

  return (
    <section className="mt-8 max-w-7xl mx-auto p-4">
      <h1 className="text-center text-primary text-4xl mb-6">Our Menu</h1>
      {Object.keys(groupedItems).length === 0 ? (
        <div className="text-center text-gray-700">No items available.</div>
      ) : (
        Object.entries(groupedItems).map(([category, items]) => (
          <div key={category} className="mb-8">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">
              {category}
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {items.map((item) => (
                <div
                  key={item.id}
                  className="bg-white p-6 rounded shadow-md flex flex-col transition-transform transform hover:scale-105 duration-300"
                >
                  {item.pictureUrl && (
                    <img
                      src={item.pictureUrl}
                      alt={item.name}
                      className="w-full h-32 object-cover rounded mb-4"
                    />
                  )}
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">
                    {item.name}
                  </h3>
                  <p className="text-gray-700 mb-2">
                    <strong>Price:</strong> ${item.price.toFixed(2)}
                  </p>
                  <ul className="list-disc list-inside text-gray-600 mb-4">
                    {item.ingredients.map((ing) => (
                      <li key={ing.id}>
                        {ing.name} - Qty: {ing.quantity} (Extra Cost: $
                        {ing.extraCost.toFixed(2)})
                      </li>
                    ))}
                  </ul>
                  <button
                    onClick={() => handleAddToCart(item)}
                    className="mt-auto bg-secondary hover:bg-secondary-dark text-white px-4 py-2 rounded transition-colors duration-300"
                  >
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        title={modalTitle}
      >
        <p>{modalMessage}</p>
      </Modal>
    </section>
  );
}

export default Menu;
