import React, { useEffect, useState } from "react";
import api from "../axiosInstance";
import monitorToken from "./monitorToken";
import { Link, useLocation } from "react-router-dom";

export default function Items() {
  const [profile, setProfile] = useState(null);
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    name: "",
    price: "",
    categoryId: "",
    ingredientIds: [],
  });
  const [categories, setCategories] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const location = useLocation();

  useEffect(() => {
    monitorToken();

    const fetchData = async () => {
      try {
        // Fetch user profile
        const { data: profileData } = await api.get("/auth/profile", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setProfile(profileData);

        // Fetch items and initialize edited fields and success state
        const { data: itemsData } = await api.get("/Item", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const itemsWithExtras = itemsData.map((item) => ({
          ...item,
          editedName: item.name,
          editedPrice: item.price,
          editedCategoryId: item.categoryId,
          editedIngredientIds: item.ingredientIds || [],
          success: false,
        }));
        setItems(itemsWithExtras);

        // Fetch categories
        const { data: categoriesData } = await api.get("/Category", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setCategories(categoriesData);

        // Fetch ingredients
        const { data: ingredientsData } = await api.get("/Ingredient", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setIngredients(ingredientsData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Handle changes to new item fields
  const handleNewItemChange = (field, value) => {
    setNewItem((prevItem) => ({
      ...prevItem,
      [field]: value,
    }));
  };

  // Handle adding/removing ingredients for new item
  const handleNewItemIngredientChange = (ingredientId) => {
    setNewItem((prevItem) => {
      const ingredientIds = prevItem.ingredientIds.includes(ingredientId)
        ? prevItem.ingredientIds.filter((id) => id !== ingredientId)
        : [...prevItem.ingredientIds, ingredientId];
      return { ...prevItem, ingredientIds };
    });
  };

  // Handle creating a new item
  const handleCreateItem = async (e) => {
    e.preventDefault();

    // Validate new item fields
    if (!newItem.name.trim()) {
      alert("Please enter an item name.");
      return;
    }
    if (!newItem.price || isNaN(newItem.price) || Number(newItem.price) <= 0) {
      alert("Please enter a valid price.");
      return;
    }
    if (!newItem.categoryId) {
      alert("Please select a category.");
      return;
    }

    try {
      // Send POST request to create item
      await api.post(
        "/Item",
        {
          name: newItem.name,
          price: Number(newItem.price),
          categoryId: Number(newItem.categoryId),
          ingredientIds: newItem.ingredientIds.map((id) => Number(id)),
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Fetch updated items list and initialize edited fields and success state
      const { data: itemsData } = await api.get("/Item", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      const itemsWithExtras = itemsData.map((item) => ({
        ...item,
        editedName: item.name,
        editedPrice: item.price,
        editedCategoryId: item.categoryId,
        editedIngredientIds: item.ingredientIds || [],
        success: false,
      }));
      setItems(itemsWithExtras);

      // Clear new item fields
      setNewItem({
        name: "",
        price: "",
        categoryId: "",
        ingredientIds: [],
      });

      alert("Item created successfully.");
    } catch (error) {
      console.error("Error creating item:", error);
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${error.response.data.detail}`);
      } else if (error.response && error.response.data) {
        alert(`Error: ${JSON.stringify(error.response.data)}`);
      } else if (error.message) {
        alert(`Error: ${error.message}`);
      } else {
        alert("An unexpected error occurred while creating the item.");
      }
    }
  };

  // Handle changes to existing items
  const handleItemChange = (index, field, value) => {
    setItems((prevItems) => {
      const updatedItems = [...prevItems];
      updatedItems[index][field] = value;
      return updatedItems;
    });
  };

  // Handle adding/removing ingredients for existing items
  const handleItemIngredientChange = (index, ingredientId) => {
    setItems((prevItems) => {
      const updatedItems = [...prevItems];
      const ingredientIds = updatedItems[index].editedIngredientIds.includes(
        ingredientId
      )
        ? updatedItems[index].editedIngredientIds.filter(
            (id) => id !== ingredientId
          )
        : [...updatedItems[index].editedIngredientIds, ingredientId];
      updatedItems[index].editedIngredientIds = ingredientIds;
      return updatedItems;
    });
  };

  // Handle updating an existing item
  const handleUpdateItem = async (id, index) => {
    const item = items[index];

    // Validate item fields
    if (!item.editedName.trim()) {
      alert("Item name cannot be empty.");
      return;
    }
    if (
      !item.editedPrice ||
      isNaN(item.editedPrice) ||
      Number(item.editedPrice) <= 0
    ) {
      alert("Please enter a valid price.");
      return;
    }
    if (!item.editedCategoryId) {
      alert("Please select a category.");
      return;
    }

    try {
      // Send PUT request to update item
      await api.put(
        `/Item/${id}`,
        {
          name: item.editedName,
          price: Number(item.editedPrice),
          categoryId: Number(item.editedCategoryId),
          ingredientIds: item.editedIngredientIds.map((id) => Number(id)),
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Update the item in the state and set success to true
      setItems((prevItems) => {
        const updatedItems = [...prevItems];
        updatedItems[index].name = item.editedName;
        updatedItems[index].price = item.editedPrice;
        updatedItems[index].categoryId = item.editedCategoryId;
        updatedItems[index].ingredientIds = item.editedIngredientIds;
        updatedItems[index].success = true;
        return updatedItems;
      });

      // Clear the success message after 3 seconds
      setTimeout(() => {
        setItems((prevItems) => {
          const updatedItems = [...prevItems];
          if (updatedItems[index]) {
            updatedItems[index].success = false;
          }
          return updatedItems;
        });
      }, 3000);
    } catch (error) {
      console.error("Error updating item:", error);
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${error.response.data.detail}`);
      } else if (error.response && error.response.data) {
        alert(`Error: ${JSON.stringify(error.response.data)}`);
      } else if (error.message) {
        alert(`Error: ${error.message}`);
      } else {
        alert("An unexpected error occurred while updating the item.");
      }
    }
  };

  // Handle deleting an item
  const handleDeleteItem = async (id) => {
    // Optional: Add a confirmation dialog
    if (!window.confirm("Are you sure you want to delete this item?")) {
      return;
    }

    try {
      await api.delete(`/Item/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });

      // Remove the deleted item from the state
      setItems((prevItems) => prevItems.filter((item) => item.id !== id));

      alert("Item deleted successfully.");
    } catch (error) {
      console.error("Error deleting item:", error);
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${error.response.data.detail}`);
      } else if (error.response && error.response.data) {
        alert(`Error: ${JSON.stringify(error.response.data)}`);
      } else if (error.message) {
        alert(`Error: ${error.message}`);
      } else {
        alert("An unexpected error occurred while deleting the item.");
      }
    }
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <section className="mt-8 max-w-7xl mx-auto">
      {profile.role === "Admin" && (
        <div className="flex mx-auto items-center justify-center gap-2 my-4 tabs">
          <Link
            className={location.pathname === "/profile" ? "active" : ""}
            to="/profile"
          >
            Profile
          </Link>
          <Link
            className={location.pathname === "/categories" ? "active" : ""}
            to="/categories"
          >
            Categories
          </Link>
          <Link
            className={location.pathname === "/items" ? "active" : ""}
            to="/items"
          >
            Items
          </Link>
          <Link
            className={location.pathname === "/ingredients" ? "active" : ""}
            to="/ingredients"
          >
            Ingredients
          </Link>
          <Link
            className={location.pathname === "/users" ? "active" : ""}
            to="/users"
          >
            Users
          </Link>
        </div>
      )}

      {/* Item Creation Form */}
      <form className="mt-8 w-1/2 mx-auto" onSubmit={handleCreateItem}>
        <h2 className="text-center text-primary text-4xl mb-4">
          Create New Item
        </h2>
        <div className="flex flex-col gap-2">
          <div>
            <label className="info">Item Name</label>
            <input
              type="text"
              value={newItem.name}
              onChange={(e) => handleNewItemChange("name", e.target.value)}
            />
          </div>
          <div>
            <label className="info">Price</label>
            <br />
            <input
              type="number"
              step="0.01"
              value={newItem.price}
              onChange={(e) => handleNewItemChange("price", e.target.value)}
            />
          </div>
          <div>
            <label className="info">Category</label>
            <br />
            <select
              value={newItem.categoryId}
              onChange={(e) =>
                handleNewItemChange("categoryId", e.target.value)
              }
            >
              <option value="">Select Category</option>
              {categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="info">Ingredients</label>
            <div className="flex flex-wrap gap-2">
              {ingredients.map((ingredient) => (
                <label key={ingredient.id} className="flex items-center">
                  <input
                    type="checkbox"
                    value={ingredient.id}
                    checked={newItem.ingredientIds.includes(ingredient.id)}
                    onChange={() =>
                      handleNewItemIngredientChange(ingredient.id)
                    }
                    className="mr-1"
                  />
                  {ingredient.name}
                </label>
              ))}
            </div>
          </div>
          <button className="px-6 py-2 mt-2" type="submit">
            Create Item
          </button>
        </div>
      </form>

      {/* Existing Items Grid */}
      <div className="mt-8">
        <h2 className="text-center text-primary text-4xl mb-4">
          Existing Items:
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {items.map((item, index) => (
            <div
              key={item.id}
              className="bg-white p-4 rounded shadow-md flex flex-col"
            >
              {/* Success Message */}
              {item.success && (
                <span className="text-green-600 mb-2">
                  Updated successfully
                </span>
              )}
              <div className="flex flex-col gap-2 flex-grow">
                <div>
                  <label className="font-semibold">Item Name</label>
                  <input
                    type="text"
                    value={item.editedName}
                    onChange={(e) =>
                      handleItemChange(index, "editedName", e.target.value)
                    }
                    className="w-full mt-1 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="font-semibold">Price</label>
                  <input
                    type="number"
                    step="0.01"
                    value={item.editedPrice}
                    onChange={(e) =>
                      handleItemChange(index, "editedPrice", e.target.value)
                    }
                    className="w-full mt-1 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="font-semibold">Category</label>
                  <select
                    value={item.editedCategoryId}
                    onChange={(e) =>
                      handleItemChange(
                        index,
                        "editedCategoryId",
                        e.target.value
                      )
                    }
                    className="w-full mt-1 p-2 border rounded"
                  >
                    <option value="">Select Category</option>
                    {categories.map((category) => (
                      <option key={category.id} value={category.id}>
                        {category.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="font-semibold">Ingredients</label>
                  <div className="flex flex-wrap gap-2 mt-1">
                    {ingredients.map((ingredient) => (
                      <label key={ingredient.id} className="flex items-center">
                        <input
                          type="checkbox"
                          value={ingredient.id}
                          checked={item.editedIngredientIds.includes(
                            ingredient.id
                          )}
                          onChange={() =>
                            handleItemIngredientChange(index, ingredient.id)
                          }
                          className="mr-1"
                        />
                        {ingredient.name}
                      </label>
                    ))}
                  </div>
                </div>
              </div>
              <div className="flex justify-between mt-4">
                <button
                  className="px-4 py-2 bg-gray-300 text-white rounded hover:bg-gray-600"
                  onClick={() => handleUpdateItem(item.id, index)}
                >
                  Update
                </button>
                <button
                  className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-700"
                  onClick={() => handleDeleteItem(item.id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
