import React, { useState, useEffect } from "react";
import { useCart } from "./CartContext";
import api from "../axiosInstance";
import ExtraIngredientsModal from "./ExtraIngredientsModal";

const Menu = () => {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isExtrasModalOpen, setIsExtrasModalOpen] = useState(false);
  const { addToCart, fetchCart } = useCart(); // Correctly importing fetchCart

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const { data } = await api.get("/Item");
        setItems(data);
      } catch (error) {
        console.error("Error fetching items:", error);
      }
    };

    fetchItems();
  }, []);

  // Opens the modal for customization
  const handleOpenExtrasModal = (item) => {
    setSelectedItem(item);
    setIsExtrasModalOpen(true);
  };

  // Adds the item with customized ingredients to the cart
  const handleAddToCartWithExtras = async (extras) => {
    try {
      const ingredientQuantities = extras.reduce((acc, ing) => {
        acc[ing.id] = ing.quantity;
        return acc;
      }, {});

      await addToCart(selectedItem.id, 1, ingredientQuantities); // Uses addToCart from context
      fetchCart(); // Fetch updated cart
    } catch (error) {
      console.error("Error adding item to cart:", error);
    } finally {
      setIsExtrasModalOpen(false);
      setSelectedItem(null);
    }
  };

  return (
    <div>
      <h1>Menu</h1>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            <h2>{item.name}</h2>
            <p>${item.price.toFixed(2)}</p>
            <button onClick={() => handleOpenExtrasModal(item)}>
              Customize & Add
            </button>
          </li>
        ))}
      </ul>

      {/* Modal for customizing ingredients */}
      {selectedItem && (
        <ExtraIngredientsModal
          item={selectedItem}
          isOpen={isExtrasModalOpen}
          onClose={() => setIsExtrasModalOpen(false)}
          onConfirm={handleAddToCartWithExtras} // Properly passing the handler
        />
      )}
    </div>
  );
};

export default Menu;
