import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Profile from './pages/Profile';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      const refreshToken = localStorage.getItem('refreshToken'); // Adjust based on your flow
      await axios.post('https://localhost:5000/api/auth/logout', { refreshToken });
      localStorage.removeItem('token');
      localStorage.removeItem('refreshToken'); // Clear refresh token as well if needed
      setIsAuthenticated(false);
      navigate('/login'); // Redirect to login page after logout
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
<Router>
      <header className="flex items-center justify-between mx-4 my-2">
        <nav className="flex items-center gap-8 text-primary font-semibold">
          <Link className="font-semibold text-2xl text-primary" to="/">
            BREWTIFUL
          </Link>
          <Link to="/">Home</Link>
          <Link to="/menu">Menu</Link>
          <Link to="/about">About</Link>
        </nav>
        <nav className="flex items-center gap-4 text-primary font-semibold">
          <Link to="/login">Login</Link>
          <Link to="/register" className="bg-primary rounded text-white px-6 py-2">
            Register
          </Link>
          <Link to="/profile">Profile</Link>
        </nav>
      </header>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>
    </Router>
  );
}

export default App;
