import React, { useEffect, useState } from "react";
import api from "../axiosInstance";
import monitorToken from "./monitorToken";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
  useLocation,
} from "react-router-dom";

export default function Categories() {
  const [profile, setProfile] = useState(null);
  const [categoryName, setCategoryName] = useState("");
  const [categories, setCategories] = useState([]);
  const location = useLocation();

  useEffect(() => {
    monitorToken();

    const fetchProfileAndCategories = async () => {
      try {
        // Fetch user profile
        const { data: profileData } = await api.get("/auth/profile", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setProfile(profileData);

        // Fetch existing categories and initialize editedName and success
        const { data: categoriesData } = await api.get("/Category", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const categoriesWithExtras = categoriesData.map((category) => ({
          ...category,
          editedName: category.name,
          success: false,
        }));
        setCategories(categoriesWithExtras);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchProfileAndCategories();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!categoryName.trim()) {
      alert("Please enter a category name.");
      return;
    }

    try {
      // Create new category
      await api.post(
        "/Category",
        { name: categoryName },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Fetch updated categories list and initialize editedName and success
      const { data: updatedCategories } = await api.get("/Category", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      const categoriesWithExtras = updatedCategories.map((category) => ({
        ...category,
        editedName: category.name,
        success: false,
      }));
      setCategories(categoriesWithExtras);

      // Clear the input field
      setCategoryName("");
    } catch (error) {
      console.error("Error creating category:", error);
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${error.response.data.detail}`);
      } else if (error.response && error.response.data) {
        alert(`Error: ${JSON.stringify(error.response.data)}`);
      } else if (error.message) {
        alert(`Error: ${error.message}`);
      } else {
        alert("An unexpected error occurred while creating the category.");
      }
    }
  };

  const handleCategoryNameChange = (index, newName) => {
    setCategories((prevCategories) => {
      const updatedCategories = [...prevCategories];
      updatedCategories[index].editedName = newName;
      return updatedCategories;
    });
  };

  const handleUpdateCategory = async (id, newName, index) => {
    if (!newName.trim()) {
      alert("Category name cannot be empty.");
      return;
    }

    try {
      // Send PUT request to update category
      await api.put(
        `/Category/${id}`,
        { name: newName },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Update the category in the state and set success to true
      setCategories((prevCategories) => {
        const updatedCategories = [...prevCategories];
        updatedCategories[index].name = newName;
        updatedCategories[index].editedName = newName;
        updatedCategories[index].success = true;
        return updatedCategories;
      });

      // Clear the success message after 3 seconds
      setTimeout(() => {
        setCategories((prevCategories) => {
          const updatedCategories = [...prevCategories];
          if (updatedCategories[index]) {
            updatedCategories[index].success = false;
          }
          return updatedCategories;
        });
      }, 3000);
    } catch (error) {
      console.error("Error updating category:", error);
      if (error.response && error.response.data && error.response.data.detail) {
        alert(`Error: ${error.response.data.detail}`);
      } else if (error.response && error.response.data) {
        alert(`Error: ${JSON.stringify(error.response.data)}`);
      } else if (error.message) {
        alert(`Error: ${error.message}`);
      } else {
        alert("An unexpected error occurred while updating the category.");
      }
    }
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <section className="mt-8 max-w-lg mx-auto">
      {profile.role === "Admin" && (
        <div className="flex mx-auto items-center justify-center gap-2 my-4 tabs">
          <Link
            className={location.pathname === "/profile" ? "active" : ""}
            to="/profile"
          >
            Profile
          </Link>
          <Link
            className={location.pathname === "/categories" ? "active" : ""}
            to="/categories"
          >
            Categories
          </Link>
          <Link
            className={location.pathname === "/items" ? "active" : ""}
            to="/items"
          >
            Items
          </Link>
          <Link
            className={location.pathname === "/ingredients" ? "active" : ""}
            to="/ingredients"
          >
            Ingredients
          </Link>
          <Link
            className={location.pathname === "/users" ? "active" : ""}
            to="/users"
          >
            Users
          </Link>
        </div>
      )}

      {/* Category Creation Form */}
      <form className="mt-8" onSubmit={handleSubmit}>
        <div className="flex gap-2 items-end">
          <div className="grow">
            <label>New category name</label>
            <input
              type="text"
              value={categoryName}
              onChange={(e) => setCategoryName(e.target.value)}
            />
          </div>
          <div className="pb-2">
            <button className="px-6 py-2" type="submit">
              Create
            </button>
          </div>
        </div>
      </form>

      {/* Existing Categories List */}
      <div className="mt-8">
        <h2 className="text-xl font-semibold">Existing Categories:</h2>
        <ul className="list-disc list-inside">
          {categories.map((category, index) => (
            <li key={category.id} className="mt-2 flex items-center gap-2">
              {/* Success Message */}
              {category.success && (
                <span className="text-green-600">Updated successfully</span>
              )}
              {/* Category Input */}
              <input
                type="text"
                value={category.editedName}
                onChange={(e) =>
                  handleCategoryNameChange(index, e.target.value)
                }
              />
              <button
                className="h-2"
                type="submit"
                onClick={() =>
                  handleUpdateCategory(category.id, category.editedName, index)
                }
              >
                Update
              </button>
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
}
