import React, { useState, useEffect } from "react";
import FlyingButton from "react-flying-item";
import axios from "axios";
import api from "../axiosInstance";

export default function Menu() {
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [items, setItems] = useState([]);
    const [cart, setCart] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [selectedExtras, setSelectedExtras] = useState([]);
    const [selectedSize, setSelectedSize] = useState(null);
    const [showPopup, setShowPopup] = useState(false);

    useEffect(() => {
        // Fetch categories
        api.get("/Category").then((response) => setCategories(response.data));
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            // Fetch items for the selected category
            api.get(`/Item/category/${selectedCategory.id}`).then((response) => setItems(response.data));
        }
    }, [selectedCategory]);

    const handleAddToCartClick = (item) => {
        setSelectedItem(item);
        setSelectedSize(item.sizes?.[0] || null);
        setSelectedExtras([]);
        setShowPopup(true);
    };

    const handleSizeChange = (size) => {
        setSelectedSize(size);
    };

    const handleExtraChange = (extra, isChecked) => {
        setSelectedExtras((prev) =>
            isChecked ? [...prev, extra] : prev.filter((e) => e._id !== extra._id)
        );
    };

    const calculatePrice = () => {
        if (!selectedItem) return 0;
        let totalPrice = selectedItem.basePrice;
        if (selectedSize) totalPrice += selectedSize.price;
        selectedExtras.forEach((extra) => (totalPrice += extra.price));
        return totalPrice;
    };

    const handleAddToOrder = () => {
        const cartItem = {
            ...selectedItem,
            size: selectedSize,
            extras: selectedExtras,
            totalPrice: calculatePrice(),
        };
        setCart((prev) => [...prev, cartItem]);
        setShowPopup(false);
    };

    const handleRemoveFromCart = (index) => {
        setCart((prev) => prev.filter((_, i) => i !== index));
    };

    return (
        <div className="menu-page flex p-4">
            {/* Filters */}
            <aside className="filters w-1/4 mr-4">
                <h3 className="text-lg font-bold">Categories</h3>
                <ul>
                    {categories.map((category) => (
                        <li key={category.id}>
                            <button
                                className="text-primary hover:underline"
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category.name}
                            </button>
                        </li>
                    ))}
                </ul>
            </aside>

            {/* Menu Items */}
            <main className="menu-items w-2/4">
                <h2 className="text-2xl font-bold mb-4">
                    {selectedCategory ? selectedCategory.name : "Select a Category"}
                </h2>
                {items.map((item) => (
                    <div
                        key={item.id}
                        className="bg-gray-200 p-4 rounded-lg text-center
                          group hover:bg-white hover:shadow-md hover:shadow-black/25 transition-all mb-4"
                    >
                        <div className="text-center">
                            <img src={item.image} alt={item.name} className="max-h-auto max-h-24 block mx-auto" />
                        </div>
                        <h4 className="font-semibold text-xl my-3">{item.name}</h4>
                        <p className="text-gray-500 text-sm line-clamp-3">{item.description}</p>
                        <div className="mt-4">
                            <FlyingButton targetTop="5%" targetLeft="95%" src={item.image}>
                                <button
                                    onClick={() => handleAddToCartClick(item)}
                                    className="bg-primary text-white rounded-full px-8 py-2"
                                >
                                    Add to cart ${item.basePrice.toFixed(2)}
                                </button>
                            </FlyingButton>
                        </div>
                    </div>
                ))}
            </main>

            {/* Cart Summary */}
            <aside className="cart-summary w-1/4 ml-4">
                <h3 className="text-lg font-bold">Cart</h3>
                {cart.map((cartItem, index) => (
                    <div key={index} className="flex items-center gap-4 border-b py-4">
                        <div className="w-24">
                            <img
                                src={cartItem.image}
                                alt={cartItem.name}
                                className="rounded max-w-full max-h-full"
                            />
                        </div>
                        <div className="grow">
                            <h3 className="font-semibold">{cartItem.name}</h3>
                            {cartItem.size && (
                                <div className="text-sm">
                                    Size: <span>{cartItem.size.name}</span>
                                </div>
                            )}
                            {cartItem.extras?.length > 0 && (
                                <div className="text-sm text-gray-500">
                                    {cartItem.extras.map((extra) => (
                                        <div key={extra._id}>
                                            {extra.name} (+${extra.price.toFixed(2)})
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                        <div className="text-lg font-semibold">${cartItem.totalPrice.toFixed(2)}</div>
                        <button
                            className="text-red-500 hover:text-red-700"
                            onClick={() => handleRemoveFromCart(index)}
                        >
                            Remove
                        </button>
                    </div>
                ))}
            </aside>

            {/* Popup */}
            {showPopup && selectedItem && (
                <div
                    className="fixed inset-0 bg-black/50 flex items-center justify-center"
                    onClick={() => setShowPopup(false)}
                >
                    <div
                        className="bg-white p-4 rounded-lg w-1/3"
                        onClick={(e) => e.stopPropagation()}
                    >
                        <img
                            src={selectedItem.image}
                            alt={selectedItem.name}
                            className="w-full h-auto rounded-lg"
                        />
                        <h3 className="text-lg font-bold text-center mt-2">{selectedItem.name}</h3>
                        <p className="text-gray-600 text-center">{selectedItem.description}</p>
                        {selectedItem.sizes?.length > 0 && (
                            <div className="mt-4">
                                <h4 className="text-center font-semibold">Choose Size</h4>
                                {selectedItem.sizes.map((size) => (
                                    <label
                                        key={size._id}
                                        className="flex items-center gap-2 p-2 border rounded-lg"
                                    >
                                        <input
                                            type="radio"
                                            name="size"
                                            checked={selectedSize?._id === size._id}
                                            onChange={() => handleSizeChange(size)}
                                        />
                                        {size.name} (+${size.price.toFixed(2)})
                                    </label>
                                ))}
                            </div>
                        )}
                        {selectedItem.extraIngredientPrices?.length > 0 && (
                            <div className="mt-4">
                                <h4 className="text-center font-semibold">Add Extras</h4>
                                {selectedItem.extraIngredientPrices.map((extra) => (
                                    <label
                                        key={extra._id}
                                        className="flex items-center gap-2 p-2 border rounded-lg"
                                    >
                                        <input
                                            type="checkbox"
                                            onChange={(e) => handleExtraChange(extra, e.target.checked)}
                                            checked={selectedExtras.some((ex) => ex._id === extra._id)}
                                        />
                                        {extra.name} (+${extra.price.toFixed(2)})
                                    </label>
                                ))}
                            </div>
                        )}
                        <div className="text-center mt-4">
                            <h4>Total Price: ${calculatePrice().toFixed(2)}</h4>
                            <button
                                className="bg-primary text-white py-2 px-4 rounded-full"
                                onClick={handleAddToOrder}
                            >
                                Add to Cart
                            </button>
                            <button
                                className="bg-gray-500 text-white py-2 px-4 rounded-full ml-2"
                                onClick={() => setShowPopup(false)}
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
