import React, { useState, useEffect } from "react";

const ExtraIngredientsModal = ({ item, isOpen, onClose, onConfirm }) => {
  const [extras, setExtras] = useState([]);
  const [totalPrice, setTotalPrice] = useState(item.price);

  useEffect(() => {
    setExtras(
      item.ingredients.map((ing) => ({
        id: ing.id,
        name: ing.name,
        quantity: ing.defaultQuantity,
        extraCost: ing.extraCost,
      }))
    );
    setTotalPrice(item.price);
  }, [item]);

  const handleQuantityChange = (id, increment) => {
    setExtras((prev) =>
      prev.map((ing) => {
        if (ing.id === id) {
          const newQuantity = ing.quantity + increment;
          const costDifference = (newQuantity - ing.quantity) * ing.extraCost;
          setTotalPrice((prevPrice) => prevPrice + costDifference);
          return {
            ...ing,
            quantity: Math.max(newQuantity, ing.defaultQuantity),
          };
        }
        return ing;
      })
    );
  };

  return (
    isOpen && (
      <div className="modal">
        <div className="modal-content">
          <h2>Customize {item.name}</h2>
          <ul>
            {extras.map((extra) => (
              <li key={extra.id}>
                <span>{extra.name}</span>
                <button onClick={() => handleQuantityChange(extra.id, -1)}>
                  -
                </button>
                <span>{extra.quantity}</span>
                <button onClick={() => handleQuantityChange(extra.id, 1)}>
                  +
                </button>
              </li>
            ))}
          </ul>
          <p>Total Price: ${totalPrice.toFixed(2)}</p>
          <button onClick={() => onConfirm(extras)}>Add to Cart</button>
          <button onClick={onClose}>Cancel</button>
        </div>
      </div>
    )
  );
};

export default ExtraIngredientsModal;
