// src/pages/Menu.js
import React, { useEffect, useState } from "react";
import api from "../axiosInstance";
import monitorToken from "./monitorToken";
import { Link } from "react-router-dom";
import { useCart } from "./CartContext";
import Modal from "../components/Modal";
import ExtraIngredientsModal from "../components/ExtraIngredientsModal";

function Menu() {
  const [profile, setProfile] = useState(null);
  const [items, setItems] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isExtrasModalOpen, setIsExtrasModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalTitle, setModalTitle] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    monitorToken();

    const fetchItems = async () => {
      try {
        // Fetch user profile
        const { data: profileData } = await api.get("/auth/profile", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setProfile(profileData);

        // Check if user is not Admin
        if (profileData.role === "Admin") {
          setModalMessage("Admins should use the Admin Orders page.");
          setModalTitle("Access Denied");
          setIsModalOpen(true);
          return;
        }

        // Fetch items
        const { data: itemsData } = await api.get("/Item", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setItems(itemsData);
      } catch (error) {
        console.error("Error fetching items:", error);
        setModalMessage("Failed to fetch menu items.");
        setModalTitle("Error");
        setIsModalOpen(true);
      }
    };

    fetchItems();
  }, []);

  // Handle opening the extras modal
  const handleOpenExtrasModal = (item) => {
    setSelectedItem(item);
    setIsExtrasModalOpen(true);
  };

  // Handle adding item with extras to cart
  const handleAddToCartWithExtras = async (extras) => {
    try {
      // Calculate extra cost
      const extraCost = extras.reduce(
        (total, ing) => total + ing.extraCost * ing.quantity,
        0
      );

      // Calculate total price
      const totalPrice = selectedItem.price + extraCost;

      // Prepare ingredientQuantities as an object
      const ingredientQuantities = extras.reduce((acc, ing) => {
        acc[ing.id] = ing.quantity;
        return acc;
      }, {});

      // Prepare the cart item
      const cartItem = {
        ItemId: selectedItem.id,
        Quantity: 1, // or dynamic based on user input
        IngredientQuantities: ingredientQuantities,
      };

      await addToCart(selectedItem, extras, ingredientQuantities, totalPrice);
      setModalMessage(`${selectedItem.name} added to cart with extras.`);
      setModalTitle("Success");
      setIsModalOpen(true);
    } catch (error) {
      console.error("Error adding to cart:", error);
      setModalMessage(
        error.response?.data?.message ||
          "An unexpected error occurred while adding the item to the cart."
      );
      setModalTitle("Error");
      setIsModalOpen(true);
    } finally {
      setIsExtrasModalOpen(false);
      setSelectedItem(null);
    }
  };

  if (!profile)
    return <div className="text-center mt-8 text-gray-700">Loading...</div>;

  return (
    <section className="mt-8 max-w-7xl mx-auto p-4">
      <h1 className="text-center text-primary text-4xl mb-6">Our Menu</h1>
      {items.length === 0 ? (
        <div className="text-center text-gray-700">No items available.</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {items.map((item) => (
            <div
              key={item.id}
              className="bg-white p-6 rounded shadow-md flex flex-col transition-transform transform hover:scale-105 duration-300"
            >
              {/* Item Image */}
              {item.pictureUrl && (
                <img
                  src={item.pictureUrl}
                  alt={item.name}
                  className="w-full h-32 object-cover rounded mb-4"
                />
              )}

              {/* Item Details */}
              <h2 className="text-xl font-semibold text-gray-800 mb-2">
                {item.name}
              </h2>
              <p className="text-gray-700 mb-2">
                <strong>Base Price:</strong> ${item.price.toFixed(2)}
              </p>
              <ul className="list-disc list-inside text-gray-600 mb-4">
                {item.ingredients.map((ing) => (
                  <li key={ing.id}>
                    {ing.name} (Default Qty: {ing.defaultQuantity}) - Extra
                    Cost: ${ing.extraCost.toFixed(2)}
                  </li>
                ))}
              </ul>
              <button
                onClick={() => handleOpenExtrasModal(item)}
                className="mt-auto bg-secondary hover:bg-secondary-dark text-white px-4 py-2 rounded transition-colors duration-300"
              >
                Customize & Add to Cart
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Feedback Modal */}
      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        title={modalTitle}
      >
        <p>{modalMessage}</p>
      </Modal>

      {/* Extra Ingredients Modal */}
      {selectedItem && (
        <ExtraIngredientsModal
          isOpen={isExtrasModalOpen}
          onClose={() => setIsExtrasModalOpen(false)}
          item={selectedItem}
          onConfirm={handleAddToCartWithExtras}
        />
      )}
    </section>
  );
}

export default Menu;
