// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { CartProvider } from "./pages/CartContext"; // Updated path
import Navbar from "/Navbar"; // Import Navbar
import Login from "/Login"; // Updated path
import Register from "/Register";
import Profile from "/Profile";
import About from "/About";
import Categories from "/Categories";
import Items from "/Items";
import Ingredients from "/Ingredients";
import Users from "/Users";
import Orders from "/Orders";
import Menu from "/Menu";
import Cart from "/Cart"; // Import Cart Page
import AdminOrders from "/AdminOrders";
import monitorToken from "/monitorToken";
import logo from "./components/logo.png"; // Ensure correct path
import background from "./components/background.png"; // Ensure correct path
import jwt_decode, { jwtDecode } from "jwt-decode";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [cartCount, setCartCount] = useState(0); // To track cart items

  useEffect(() => {
    monitorToken();
    const token = localStorage.getItem("token");
    if (token) {
      setIsAuthenticated(true);
      try {
        const decodedToken = jwtDecode(token);
        setUserRole(decodedToken.role); // Assuming the token has a 'role' claim
      } catch (error) {
        console.error("Failed to decode token:", error);
        setUserRole(null);
        setIsAuthenticated(false);
        localStorage.removeItem("token");
      }
    } else {
      setIsAuthenticated(false);
      setUserRole(null);
    }
  }, []);

  const handleLogout = async () => {
    try {
      await fetch("api/auth/logout", {
        method: "POST",
        credentials: "include",
      });

      localStorage.removeItem("token");
      setIsAuthenticated(false);
      setUserRole(null);
      setCartCount(0);
    } catch (error) {
      console.error("Error during logout:", error);
    }
  };

  return (
    <CartProvider setCartCount={setCartCount}>
      <Router>
        <div className="flex flex-col min-h-screen font-sans">
          {/* Header */}
          <Navbar
            isAuthenticated={isAuthenticated}
            userRole={userRole}
            handleLogout={handleLogout}
            cartCount={cartCount}
          />

          {/* Main Content */}
          <main className="flex-grow bg-background p-6">
            <Routes>
              <Route
                path="/"
                element={
                  <section className="flex items-center justify-center h-full">
                    <div
                      style={{
                        backgroundImage: `url(${background})`,
                        backgroundSize: "cover",
                        backgroundPosition: "center",
                        height: "90vh",
                        width: "100%",
                      }}
                      className="rounded-lg shadow-lg"
                    ></div>
                  </section>
                }
              />
              <Route path="/about" element={<About />} />
              <Route path="/menu" element={<Menu />} />
              <Route path="/cart" element={<Cart />} />
              <Route path="/admin/orders" element={<AdminOrders />} />
              <Route
                path="/login"
                element={<Login setIsAuthenticated={setIsAuthenticated} />}
              />
              <Route path="/register" element={<Register />} />
              <Route
                path="/profile/*"
                element={
                  isAuthenticated ? (
                    <Profile />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        Please login to view your profile.
                      </h1>
                    </div>
                  )
                }
              />
              <Route
                path="/categories"
                element={
                  isAuthenticated ? (
                    <Categories />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        You do not have permission to view this page.
                      </h1>
                    </div>
                  )
                }
              />
              <Route
                path="/items"
                element={
                  isAuthenticated ? (
                    <Items />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        You do not have permission to view this page.
                      </h1>
                    </div>
                  )
                }
              />
              <Route
                path="/ingredients"
                element={
                  isAuthenticated ? (
                    <Ingredients />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        You do not have permission to view this page.
                      </h1>
                    </div>
                  )
                }
              />
              <Route
                path="/users"
                element={
                  isAuthenticated ? (
                    <Users />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        You do not have permission to view this page.
                      </h1>
                    </div>
                  )
                }
              />
              <Route
                path="/orders"
                element={
                  isAuthenticated ? (
                    <Orders />
                  ) : (
                    <div className="text-center mt-8 text-red-600">
                      <h1 className="font-bold text-xl">
                        You do not have permission to view this page.
                      </h1>
                    </div>
                  )
                }
              />
            </Routes>
          </main>

          {/* Footer */}
          <footer className="bg-primary text-white text-center py-4 shadow-inner">
            <div className="container mx-auto">
              <p className="text-sm">
                © 2024 Brewtiful - Domas Gladkauskas IFK-2.
              </p>
            </div>
          </footer>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
