import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token'); // Adjust token storage as per your authentication flow
        console.log("Token:", localStorage.getItem('token')); // Add this to debug
        const response = await axios.get('https://localhost:5000/api/auth/profile', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setProfile(response.data);
      } catch (err) {
        // More robust error handling
        let errorMessage = 'Failed to fetch profile information';
        if (err.response && err.response.data && err.response.data.message) {
          errorMessage += `: ${err.response.data.message}`;
        } else if (err.message) {
          errorMessage += `: ${err.message}`;
        } else {
          errorMessage += ': Unknown error';
        }
        setError(errorMessage);
      }
    };

    fetchProfile();
  }, []);

  if (error) return <div>{error}</div>;
  if (!profile) return <div>Loading...</div>;

  return (
    <div className="profile">
      <h1>{profile.name}</h1>
      <p>Email: {profile.email}</p>
      <p>Username: {profile.username}</p>
      <p>Role: {profile.role}</p>
    </div>
  );
};

export default Profile;
