// src/components/FilterPanel.js
import React, { useState } from "react";
import { Accordion, Form, Button } from "react-bootstrap";

const FilterPanel = ({
  categories,
  ingredients,
  selectedCategory,
  setSelectedCategory,
  filters,
  setFilters,
}) => {
  const handleCategoryChange = (e) => {
    const categoryId = parseInt(e.target.value);
    const category = categories.find((cat) => cat.Id === categoryId);
    setSelectedCategory(category);
  };

  const handleItemFilterChange = (e) => {
    const itemId = parseInt(e.target.value);
    const isChecked = e.target.checked;
    setFilters((prev) => {
      const newItems = isChecked
        ? [...prev.items, itemId]
        : prev.items.filter((id) => id !== itemId);
      return { ...prev, items: newItems };
    });
  };

  const handleIngredientFilterChange = (e) => {
    const ingredientId = parseInt(e.target.value);
    const isChecked = e.target.checked;
    setFilters((prev) => {
      const newIngredients = isChecked
        ? [...prev.ingredients, ingredientId]
        : prev.ingredients.filter((id) => id !== ingredientId);
      return { ...prev, ingredients: newIngredients };
    });
  };

  return (
    <Accordion defaultActiveKey="0" className="mt-4">
      <Accordion.Item eventKey="0">
        <Accordion.Header>Categories</Accordion.Header>
        <Accordion.Body>
          <Form>
            {categories.map((category) => (
              <Form.Check
                key={category.Id}
                type="radio"
                label={category.Name}
                name="category"
                value={category.Id}
                checked={selectedCategory?.Id === category.Id}
                onChange={handleCategoryChange}
              />
            ))}
          </Form>
        </Accordion.Body>
      </Accordion.Item>
      <Accordion.Item eventKey="1">
        <Accordion.Header>Items</Accordion.Header>
        <Accordion.Body>
          <Form>
            {/* Assuming items are part of the selected category */}
            {selectedCategory?.Items?.map((item) => (
              <Form.Check
                key={item.Id}
                type="checkbox"
                label={item.Name}
                value={item.Id}
                checked={filters.items.includes(item.Id)}
                onChange={handleItemFilterChange}
              />
            ))}
          </Form>
        </Accordion.Body>
      </Accordion.Item>
      <Accordion.Item eventKey="2">
        <Accordion.Header>Ingredients</Accordion.Header>
        <Accordion.Body>
          <Form>
            {ingredients.map((ingredient) => (
              <Form.Check
                key={ingredient.Id}
                type="checkbox"
                label={ingredient.Name}
                value={ingredient.Id}
                checked={filters.ingredients.includes(ingredient.Id)}
                onChange={handleIngredientFilterChange}
              />
            ))}
          </Form>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  );
};

export default FilterPanel;
