import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Profile from './pages/Profile';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check for token in localStorage to set initial authentication state
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  }, []);

  return (
    <Router>
      <header className="flex items-center justify-between mx-4 my-2">
        <nav className="flex items-center gap-8 text-primary font-semibold">
          <Link className="font-semibold text-2xl text-primary" to="/">BREWTIFUL</Link>
          <Link to="/">Home</Link>
          <Link to="/menu">Menu</Link>
          <Link to="/about">About</Link>
        </nav>
        <nav className="flex items-center gap-4 text-primary font-semibold">
          {isAuthenticated ? (
            <>
              <Link to="/profile">Profile</Link>
              <button className="bg-primary rounded text-white px-6 py-2">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register" className="bg-primary rounded text-white px-6 py-2">Register</Link>
            </>
          )}
        </nav>
      </header>
      <Routes>
        <Route path="/" element={<h1>Welcome to Brewtiful!</h1>} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/profile" element={isAuthenticated ? <Profile /> : <h1>Please login to view your profile.</h1>} />
      </Routes>
    </Router>
  );
}

export default App;
